import React, { useState, useCallback, memo } from "react";
import { useLazyGetSummaryQuery } from "../services/article";
import { useLoadArticles } from "../customHooks";
import { copy, linkIcon, loader, logo, tick } from "../assets";
import debounce from "lodash.debounce";
import * as pdfMake from "pdfmake/build/pdfmake";
import * as pdfFonts from 'pdfmake/build/vfs_fonts';
pdfMake.vfs = pdfFonts.pdfMake.vfs;

const logoBase64 = "data:image/png;base64,"; // Replace PUT_BASE64_STRING_HERE with the base64 string from step 1
    
const Demo = () => {
  const [article, setArticle] = useState({
    url: "",
    summary: "",
  });
  const [copied, setCopied] = useState("");
  const [allArticles, setAllArticles] = useLoadArticles();

  const [getSummary, { error, isFetching }] = useLazyGetSummaryQuery();

  const updateLocalStorage = (articles) => {
    localStorage.setItem("articles", JSON.stringify(articles));
  };

  const handleSubmit = useCallback(
    debounce(async (e) => {
      e.preventDefault();

      const existingArticle = allArticles.find(
        (item) => item.url === article.url
      );

      if (existingArticle) return setArticle(existingArticle);

      const { data } = await getSummary({ articleUrl: article.url });
      if (data?.summary) {
        const newArticle = { ...article, summary: data.summary };
        const updatedAllArticles = [newArticle, ...allArticles];

        setArticle(newArticle);
        setAllArticles(updatedAllArticles);
        updateLocalStorage(updatedAllArticles);
      }
    }, 300),
    [allArticles, getSummary, article.url]
  );

  const handleCopy = useCallback((copyUrl) => {
    setCopied(copyUrl);
    navigator.clipboard.writeText(copyUrl);
    setTimeout(() => setCopied(false), 3000);
  }, []);

  const handleKeyDown = useCallback(
    (e) => {
      if (e.keyCode === 13) {
        handleSubmit(e);
      }
    },
    [handleSubmit]
  );

  {/*Export as pdf*/}
  const exportToEditablePdf = () => {
    if (!article.summary) return;
    const docDefinition = {
      content: [
        {
          image: 'logo',
          width: 130, // Adjust the width as needed
          margin: [0, 0, 0, 8],
          
        },

        {
          text: "Article Summary",
          fontSize: 18,
          bold: true,
          margin: [0, 0, 0, 8],
        },

        {
          text: article.summary,
          fontSize: 12,
        },

        {
          text: "\n\n\n\n\Thank you for choosing Littlebird.",
          fontSize: 14,
          bold: true,
          margin: [0, 0, 0, 8],
          alignment: 'center',
        },

      ],
      images: {
        logo: logoBase64,
      },
    };
    
    
  
    pdfMake.createPdf(docDefinition).download("article_summary.pdf");
  };
  

  

  
  

  return (
    <section className="mt-16 w-full max-w-xl">
      {/* Search */}
      <div className="flex flex-col w-full gap-2">
        <form
          className="relative flex justify-center items-center"
          onSubmit={handleSubmit}
        >
          <img
            src={linkIcon}
            alt="link-icon"
            className="absolute left-0 my-2 ml-3 w-5"
          />

          <input
            type="url"
            placeholder="Paste the article link"
            value={article.url}
            onChange={(e) =>
              setArticle({ ...article, url: e.target.value })
            }
            onKeyDown={handleKeyDown}
            required
            className="url_input peer"
          />
          <button
            type="submit"
            className="submit_btn peer-focus:border-gray-700 peer-focus:text-gray-700"
          >
            <p>↩</p>
          </button>
        </form>

        

        {/* Browse History */}
        <div className="flex flex-col gap-1 max-h-60 overflow-y-auto">
          {allArticles.slice().reverse().map((item) => (
            <div
              key={`link-${item.url}`}
              onClick={() => setArticle(item)}
              className="link_card"
            >
              <div className="copy_btn" onClick={() => handleCopy(item.url)}>
                <img
                  src={copied === item.url ? tick : copy}
                  alt={copied === item.url ? "tick_icon" : "copy_icon"}
                  className="w-[40%] h-[40%] object-contain"
                />
              </div>
              <p className="flex-1 font-satoshi text-blue-700 font-medium text-sm truncate">
                {item.url}
              </p>
            </div>
          ))}
          </div>
        </div>

{/* Display Result */}
<div className="my-10 max-w-full flex justify-center items-center">
  {isFetching ? (
    <img src={loader} alt="Loading..." className="w-20 h-20 object-contain" />
  ) : error ? (
    <p className="font-inter font-bold text-black text-center">
      Well, that wasn't supposed to happen...
      <br />
      <span className="font-satoshi font-normal text-gray-700">
        {error?.data?.error}
      </span>
    </p>
  ) : (
    article.summary && (
      <div className="flex flex-col gap-3">
        <h2 className="font-satoshi font-bold text-gray-600 text-xl">
          Article <span className="blue_gradient">Summary</span>
        </h2>

      

<button type="button" onClick={exportToEditablePdf} className="black_btn">
  Export to Editable PDF
</button>


        <div className="summary_box" id="summary-content">
          <p className="font-inter font-medium text-sm text-gray-700">
            {article.summary}
          </p>
        </div>
      </div>
    )
  )}
</div>
</section>
);
};

export default memo(Demo);